const CheckOut = (props) => {
  const {setCart, cart} = props
  const getTotal = () => {
    return cart.reduce((total, item) => (total + item.price), 0)
  }
  const removeCartItem = (item) => {
    const removedCart = cart.filter(cartItem => cartItem !== item)
    setCart(removedCart)
  }

  return (
    <div>
      <h2 className='checkout-title'>CHECKOUT</h2>
      <button onClick={props.toMainPage}>back to the goods!</button>
      <div className="checkout-container">
        {cart.length === 0 && <div className='no-items'>There are no items in your cart.</div>}
        {props.cart.map((item) => 
            <div className="checkout-card" key={`${item.id}`}>
              <img className='img checkout' alt={item.name} src={item.url}></img>
              <div className="checkout-card-right">
                <h3>{item.name}</h3>
                <h4 className='price'>${item.price}</h4>
                  <button className='btn' onClick={() => removeCartItem(item)}>REMOVE</button>
              </div>
            </div>
        )}
      </div>
      <p>cost of items: </p>
      <p>TOTAL: $ {getTotal()}</p>
      <button>PayPal</button>
    </div>
  )
}
const MainPage = (props) => {
  const [email, setEmail] = useState({})
  const bottomMessage = 'Hear about our latest creations! ---> '
  const emailHandler = (e) => {
    console.log(e.target.value, 'value')
    console.log(email, 'email');
    const name = e.target.name
    const value = e.target.value
    setEmail({...email, [name]: value })
  }

  return (
    <div className='client-container'>
      <div className="top-bar">
        <div className='top-bar_spacer'></div>
        <h1 className='title'>E-Commerce</h1>
        <button onClick={props.toCheckout} className='checkout-button'>To Checkout</button>
      </div>
      <div className="inventory-container">
        {props.inventory.map((item, index) =>
          <div className='item-card' key={`${index}-item-card`}>
            <img className='img' alt={item.name} src={item.url}></img>
            <h3>{item.name}</h3>
            <h4 className='price'>${item.price}</h4>
            <div className="add-item-container">
              <button className='btn' onClick={() => props.addToCart(item)}>add to cart</button>
            </div>
          </div>
        )}
      </div>
      <div className="footer">
        <div className="thankyou">Thank you for visiting our online shop.</div>
        <div className="email-container">
          <label htmlFor="email">{bottomMessage}</label>
          <input type="email" name="email" className="email" placeholder='____ your email :D' id="" onChange={emailHandler} />
          <input type="button" value="Submit" />
        </div>
      </div>
    </div>
  )
}
function App() {
  const [inventory] = useState(INVENTORY)
  const [isCheckout, setCheckout] = useState(false)
  const [cart, setCart] = useState([])

  useEffect(() => {
    (async () => {
      const result = await request.get('/api/inventory')
    })();
  }, []);

  const addToCart = (item) => {
    if (cart.some(merch => merch.id === item.id)) {
      alert('Sorry each item is unique, and can only be added once.')
    } else {
      setCart([...cart, { ...item }])
    }
  }

  return (
    <div className='App'>
      {isCheckout
        ? <CheckOut
          cart={cart}
          toMainPage={() => setCheckout(false)}
          setCart={setCart}
        />
        : <MainPage
          inventory={inventory}
          toCheckout={() => setCheckout(true)}
          addToCart={addToCart}
        />
      }
    </div>
  )

}

export default App;
